import React, {useEffect, useState} from "react";
import {Form} from "./Form";


export const Show = ({userInput}) => {

    const [person, setPerson] = useState([])
    const [addPerson, setAddPerson] = useState([''])


    const handleFormSubmit = () => {
        fetch('/create', {
            method: 'Post',
            body: JSON.stringify({
                content: addPerson
            })

        })
    }
    useEffect(() => {
        fetch('/').then(response => {
            if (response.ok) {
                return response.json()
            }
        }).then(data => setPerson(data))
    }, [])

    return (
        <>
            <Form userInput={addPerson} onFormChange={(inputValue) => setAddPerson(inputValue)} onFormSubmit={handleFormSubmit()}/>
        </>
    )

}
